mapper.select:

select 

from t_app_info
where channel_id = #{channelId,jdbcType=INTEGER}
  and app_id = #{appId,jdbcType=INTEGER}

mapper.delete:

delete from t_app_info
where channel_id = #{channelId,jdbcType=INTEGER}
  and app_id = #{appId,jdbcType=INTEGER}

mapper.select:

select

from t_app_info
where channel_id = #{channel,jdbcType=INTEGER}

	and status = #{status,jdbcType=INTEGER}


	and title = #{name,jdbcType=VARCHAR}

order by order_flag asc


select count(1) from t_app_info
where channel_id = #{channel,jdbcType=INTEGER}

	and status = #{status,jdbcType=INTEGER}


	and title = #{name,jdbcType=INTEGER}
