mapper.select:

select 
account, id, display_name, mobile, channel, channel_account, balance, 
extract_balance, status, update_time, create_time, active_days, sum_recharge
from t_user where channel = #{channel,jdbcType=INTEGER}

    and status = #{status,jdbcType=INTEGER}


    display_name = #{displayName,jdbcType=VARCHAR}


    and mobile = #{mobile,jdbcType=VARCHAR}


    and account = #{account,jdbcType=VARCHAR}


    and create_time >= #{createTimeStart,jdbcType=TIMESTAMP}


    and create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


    and update_time >= #{updateTimeStart,jdbcType=TIMESTAMP}


    and update_time <= #{updateTimeEnd,jdbcType=TIMESTAMP}


    and balance <= #{balanceMax,jdbcType=DOUBLE}


    and balance >= #{balanceMin,jdbcType=DOUBLE}


    and sum_recharge <= #{rechargeMax,jdbcType=DOUBLE}


    and sum_recharge >= #{rechargeMin,jdbcType=DOUBLE}

 order by id asc


select count(1) from t_user where channel = #{channel,jdbcType=INTEGER}

    and status = #{status,jdbcType=INTEGER}


    display_name = #{displayName,jdbcType=VARCHAR}


    and mobile = #{mobile,jdbcType=VARCHAR}


    and account = #{account,jdbcType=VARCHAR}


    and create_time >= #{createTimeStart,jdbcType=TIMESTAMP}


    and create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}


    and update_time >= #{updateTimeStart,jdbcType=TIMESTAMP}


    and update_time <= #{updateTimeEnd,jdbcType=TIMESTAMP}


    and balance <= #{balanceMax,jdbcType=DOUBLE}


    and balance >= #{balanceMin,jdbcType=DOUBLE}


    and sum_recharge <= #{rechargeMax,jdbcType=DOUBLE}


    and sum_recharge >= #{rechargeMin,jdbcType=DOUBLE}
